import React from 'react'
import { useRef, useState, useEffect } from 'react';
import axios from '../api/axios';
import Badge from 'react-bootstrap/Badge';
import ListGroup from 'react-bootstrap/ListGroup';
import Swal from 'sweetalert2';

import EditExpense from './EditExpense';

const StyledSwal = Swal.mixin({
    customClass: {
      container: 'container-class',
      popup: 'popup-class',
      header: 'header-class',
      title: 'p-card-title',
      content: 'content-class',
      closeButton: 'close-button-class',
      image: 'image-class',
      input: 'input-class',
      actions: 'actions-class',
      confirmButton: 'p-button p-button-raised p-button-danger p-button-text-icon-left',
      cancelButton: 'p-button p-button-raised p-button-info p-button-text-icon-left',
      footer: 'footer-class'
    },
    buttonsStyling: false
  });

export default function ListExpense(props) {
    const URL = 'api/expense/store';
    const [expenseList, getExpenseList] = useState('');

    const [show, setShow] = useState(false);
    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);

    useEffect(() => {
        getExpenseList(props.expenseList);
        console.log(props.expenseList);
        console.log(expenseList);


    }, [props.expenseList]);

    const deleteExpense = async (data) => {
        // console.log(data);
        StyledSwal.fire({
          title: 'Are you sure?',
          text: `Confirm to delete expense: ${data.title}.`,
          icon: 'warning',
          showCancelButton: true,
          confirmButtonText: '<span className="pi pi-trash p-button-icon-left btn btn-danger"></span><span className="p-button-text">Delete</span>',
          cancelButtonText: '<span className="pi pi-ban p-button-icon-left"></span><span className="p-button-text">No</span>',
          // confirmButtonColor: '#f76452',
          // cancelButtonColor: '#3085d6',
          focusConfirm: false,
          focusCancel: true
        })
          .then((result) => {
            if (result.id) {

                try {
                    const response = axios.post(URL,
                       
                        JSON.stringify({ title, cost, date, category, description }),
                        {
                          headers: {
                            Authorization: token,
                            'Content-Type': 'application/json'
                          },
                          withCredentials: true
                        }
                    );
                  setSuccess(true);
                  setSuccessMessage("Expense added successfully!");

                }catch(error){
                  setErrorMessage("Something went wrong!");
            console.log(error);
                }

            }
          });
      };

  return (
    <div className='container mt-2 w-50'>
        <h3>Expense List</h3>
    <ListGroup as="ol" numbered>
          {expenseList.length > 0?

          expenseList.map((result, index) => {
            return (
            
                <ListGroup.Item
                as="li"
                className="d-flex justify-content-between align-items-start"
                >
                <div className="ms-2 me-auto">
                    <div className="fw-bold">{result.title}</div>
                    {result.category}
                    <div>{result.date}</div>
                    <div>{result.description}</div>
                    <button className='btn btn-primary btn-sm' onClick={handleShow}>Edit</button>
                    <a className='btn btn-danger btn-sm ml-2' onClick={() => deleteExpense(result)}>Delete</a>
                </div>
                <Badge bg="success" pill>
                    {result.cost} AED
                    
                </Badge>
                
                </ListGroup.Item>
              
            )
          }):
          "No record found"
        }
</ListGroup>
        <EditExpense show = {show} handleClose = {handleClose}></EditExpense>
</div>
             
  
  )
}
