import React from 'react'
import { useRef, useState, useEffect } from 'react';
import axios from '../api/axios';
import Badge from 'react-bootstrap/Badge';
import ListGroup from 'react-bootstrap/ListGroup';

import EditExpense from './EditExpense';

export default function ListExpense(props) {
    const URL = 'api/expense/store';
    const [expenseList, getExpenseList] = useState('');

    const [title, setTitle] = useState('');
    const titleRef = useRef();

    const [cost, setCost] = useState('');
    const costRef = useRef();

    const [date, setDate] = useState('');
    const dateRef = useRef();

    const [category, setCategory] = useState('');
    const categoryRef = useRef();

    const [description, setDescription] = useState('');
    const descriptionRef = useRef();

    const [show, setShow] = useState(false);
    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);

    useEffect(() => {
        getExpenseList(props.expenseList);
        console.log(props.expenseList);
        console.log(expenseList);


    }, [props.expenseList]);

    // $(document).ready(function () {
    //     setTimeout(function(){
    //      } ,1000);
    // });

  return (
    <div className='container mt-2 w-50'>
        <h3>Expense List</h3>
    <ListGroup as="ol" numbered>
          {expenseList.length > 0?

          expenseList.map((result, index) => {
            return (
            
                <ListGroup.Item
                as="li"
                className="d-flex justify-content-between align-items-start"
                >
                <div className="ms-2 me-auto">
                    <div className="fw-bold">{result.title}</div>
                    {result.category}
                    <div>{result.date}</div>
                    <div>{result.description}</div>
                    <button className='btn btn-primary btn-sm' onClick={handleShow}>Edit</button>
                    <a className='btn btn-danger btn-sm' onClick={() => deleteExpense(result)}>Delete</a>
                </div>
                <Badge bg="success" pill>
                    {result.cost} AED
                    
                </Badge>
                
                </ListGroup.Item>
              
            )
          }):
          "No record found"
        }
</ListGroup>
        <EditExpense show = {show} handleClose = {handleClose}></EditExpense>
</div>
             
  
  )
}
