import React, { useState } from 'react';
import PropTypes from 'prop-types'

import {
  MDBContainer,
  MDBNavbar,
  MDBNavbarBrand,
  MDBNavbarToggler,
  MDBIcon,
  MDBNavbarNav,
  MDBNavbarItem,
  MDBNavbarLink,
  MDBBtn,
  MDBDropdown,
  MDBDropdownToggle,
  MDBDropdownMenu,
  MDBDropdownItem,
  MDBCollapse,
} from 'mdb-react-ui-kit';

import { Link } from 'react-router-dom';
import { useSignOut, useAuthUser } from 'react-auth-kit'

export default function Header() {
  const [showBasic, setShowBasic] = useState(false);
  // const signOut = useSignOut()
  const auth = useAuthUser();
  const [query, setQuery] = useState('');
  
  const [signOutResult, signOut] = useSignOut()

  async function onClickSignOut() {
    const result = await signOut()
  }

  const submitSearch = (e) => {
    e.preventDefault();
   
  }

  return (

    <MDBNavbar expand='lg' light style={{ backgroundColor: '#e3f2fd' }}>
      <MDBContainer fluid>
        <MDBNavbarBrand>Expense Calculator</MDBNavbarBrand>

        <MDBNavbarToggler
          aria-controls='navbarSupportedContent'
          aria-expanded='false'
          aria-label='Toggle navigation'
          onClick={() => setShowBasic(!showBasic)}
        >
          <MDBIcon icon='bars' fas />
        </MDBNavbarToggler>

        <MDBCollapse navbar show={showBasic}>
          <MDBNavbarNav className='mr-auto mb-2 mb-lg-0'>
            <MDBNavbarItem>
            <Link to='/'> <MDBNavbarLink active aria-current='page'>
              Home
              </MDBNavbarLink></Link>
            </MDBNavbarItem>
            <MDBNavbarItem>
              <MDBNavbarLink href='/'>Categories</MDBNavbarLink>
            </MDBNavbarItem>

            <MDBNavbarItem className='ms-auto'>
              <MDBDropdown>
                <MDBDropdownToggle tag='a' className='nav-link' role='button'>
                {auth().name}
                </MDBDropdownToggle>
                <MDBDropdownMenu>
                  <MDBDropdownItem link>Settings</MDBDropdownItem>
                  <MDBDropdownItem link onClick={() => onClickSignOut()}>Logout</MDBDropdownItem>
                </MDBDropdownMenu>
              </MDBDropdown>
            </MDBNavbarItem>

          </MDBNavbarNav>

        </MDBCollapse>
      </MDBContainer>
    </MDBNavbar>

  )
}
