import React, {useEffect, useState} from 'react'
import ListExpense from './ListExpense'
import AddExpense from './AddExpense'
import {useAuthHeader} from 'react-auth-kit'
import axios from '../api/axios';


export default function Home(props) {
  const authHeader = useAuthHeader();
  const token = authHeader();

  const [expenseList, setExpenseList] = useState('');


  const getExpenseList = async() => {
    try {
        const URL = 'api/expense/list';
        const filter = "";

        const response = await axios.post(URL,
           
            JSON.stringify({ filter }),
            {
              headers: {
                Authorization: token,
                'Content-Type': 'application/json'
              },
              withCredentials: true
            }
        );
        console.log(response.data.data);
        setExpenseList(response.data.data);
    }catch(error){
      console.log(error);
    }
}
useEffect(() => {
  getExpenseList();
}, []);

const generatePDF = async() => {
  try {
      const URL = 'api/expense/generate-pdf';
      const filter = "";

    await axios.post(URL,
         
          JSON.stringify({ filter }),
          {
            headers: {
              Authorization: token
            },
            responseType: 'blob',
            withCredentials: true
          }
      ).then((response) => {
        console.log(response);
        const url = window.URL.createObjectURL(new Blob([response.data]));
        const link = document.createElement('a');
        link.href = url;
        link.setAttribute('download', 'expense.pdf');
        document.body.appendChild(link);
        link.click();
      });
  }catch(error){
    console.log(error);
  }
}

  return (
    <div>
      <ListExpense expenseList = {expenseList} getExpenseList = {getExpenseList} token = {token}></ListExpense>
      <button onClick={generatePDF}>Generate PDF</button>
      {/* <AddExpense token = {token}></AddExpense> */}

    </div>
  )
}
