import React from 'react'
import { useRef, useState, useEffect } from 'react';
import axios from '../api/axios';
import Badge from 'react-bootstrap/Badge';
import ListGroup from 'react-bootstrap/ListGroup';

export default function ListExpense(props) {
    const URL = 'api/expense/store';
    const [expenseList, getExpenseList] = useState('');

    useEffect(() => {
        getExpenseList(props.expenseList);
        console.log(props.expenseList);
        console.log(expenseList);


    }, [props.expenseList]);

    // $(document).ready(function () {
    //     setTimeout(function(){
    //      } ,1000);
    // });

  return (
    <div className='container mt-2 w-50'>
        <h3>Expense List</h3>
    <ListGroup as="ol" numbered>
          {expenseList.length > 0?

          expenseList.map((result, index) => {
            return (
            
                <ListGroup.Item
                as="li"
                className="d-flex justify-content-between align-items-start mt-2"
                >
                <div className="ms-2 me-auto">
                    <div className="fw-bold">{result.title}</div>
                    {result.category}
                    <div>{result.date}</div>
                    <div>{result.description}</div>
                </div>
                <Badge bg="primary" pill>
                    {result.cost} AED
                </Badge>
                </ListGroup.Item>
              
            )
          }):
          "No record found"
        }
</ListGroup>
        
</div>
             
  
  )
}
