import React from 'react'
import Form from 'react-bootstrap/Form';
import { useRef, useState, useEffect } from 'react';
import axios from '../../api/axios';

export default function AddExpense() {
    const URL = '/store';
    const [title, setTitle] = useState('');
    const titleRef = useRef();

    const [success, setSuccess] = useState(false);
    const [error, setError] = useState(false);
    const [errorMessage, setErrorMessage] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError(false);
    try {
        const response = await axios.post(URL,
            JSON.stringify({ email }),
            {
                headers: { 'Content-Type': 'application/json' },
                withCredentials: true
            }
        );
      setSuccess(true);
    }catch(error){
      setErrorMessage("Something went wrong!");
console.log(error);
    }

}
  return (
    <div className='container new-expense-container'>
        <h3>Add new expense</h3>

        <Form onSubmit={handleSubmit}>
      <Form.Group className="mb-3" >
        <Form.Label>Expense Title</Form.Label>
        <Form.Control type="text" placeholder="Enter Title" ref={titleRef}
        autoComplete="off" onChange={(e) => setTitle(e.target.value)} value={title} id='title' name='title' />
      </Form.Group>

      <Form.Group className="mb-3" >
        <Form.Label>Cost</Form.Label>
        <Form.Control type="number" placeholder="Enter cost" id='cost' name='cost' />
      </Form.Group>

      <Form.Group className="mb-3" >
        <Form.Label>Expense Date</Form.Label>
        <Form.Control type="date" placeholder="Select Date" id='date' name='date' />
      </Form.Group>

      <Form.Group className="mb-3" >
        <Form.Label>Expense Category</Form.Label>
        <Form.Control type="text" placeholder="Enter Category" id='category' name='category' />
      </Form.Group>

      <Form.Group className="mb-3">
        <Form.Label>Description</Form.Label>
        <Form.Control as="textarea" rows={3} id='description' name='description' />
      </Form.Group>

      <button className="btn btn-primary" color='info' size='lg'>Submit</button>

    </Form>
      
    </div>
  )
}
